            fn limit_duration(dur: Option<Duration>, max: Option<Duration>) -> Option<Duration> {
                match (dur, max) {
                    (dur, None) | (None, dur) => dur,
                    (Some(dur), Some(max)) => Some(dur.min(max)),
                }
            }
