                /// ```
                /// use num_traits::float::FloatCore;
                /// use ::{f32, f64};
                ///
                /// fn check<T: FloatCore>(x: T, y: T, max: T) {
                ///     assert!(x.max(y) == max);
                /// }
                ///
                /// check(1.0f32, 2.0, 2.0);
                /// check(1.0f32, f32::NAN, 1.0);
                /// check(-1.0f64, 2.0, 2.0);
                /// check(-1.0f64, f64::NAN, -1.0);
