                /// ```
                /// use num_traits::Float;
                /// use ::f64;
                ///
                /// let x = f64::consts::PI/4.0;
                /// let f = x.sin_cos();
                ///
                /// let abs_difference_0 = (f.0 - x.sin()).abs();
                /// let abs_difference_1 = (f.1 - x.cos()).abs();
                ///
                /// assert!(abs_difference_0 < 1e-10);
                /// assert!(abs_difference_0 < 1e-10);
                /// ```
                fn sin_cos(self) -> (Self, Self);

