                ///
                /// ```
                /// use num_traits::Float;
                ///
                /// let x = 7.0;
                ///
                ///
                /// let abs_difference = (x.ln().exp_m1() - 6.0).abs();
                ///
                /// assert!(abs_difference < 1e-10);
                /// ```
                fn exp_m1(self) -> Self;

