                /// } else {
                ///     assert_eq!(n.to_le(), n.swap_bytes())
                /// }
                /// ```
                fn to_le(self) -> Self;

                /// Raises self to the power of `exp`, using exponentiation by squaring.
                /// use num_traits::PrimInt;
                ///
                /// assert_eq!(2i32.pow(4), 16);
                /// ```
                fn pow(self, exp: u32) -> Self;
